		log(">> " + name);

		if (!obj) {
			log("    * undefined *");
			return;
		}

		for (var p in obj) {
			props.add(p)
		};
		var propList = props.toArray();
		java.util.Arrays.sort(propList);
		for (var prop in propList) {
			log( "   " + prop + ": " + obj[prop]);
		}
	}

