	var timing = timingMap[itemName];
	var seconds = Math.round(timing/1000);
	var minutes = Math.floor(seconds / 60);
	var secondsAfter = seconds % 60;
	log("    " + LJ(itemName, 65, i) 
				+ RJ(minutes, 11, i) + " min"
				+ RJ(secondsAfter, 3) + " sec");
	i++;
}

