function fixClient(txt, token) {
	var tokenLen = token.length;
	var p = 1;
	do {
		p = txt.indexOf(token);
		if (p >= 0) {
			txt = txt.substring(0, p) + "Copacabana" + txt.substring(p+tokenLen);
		}
	} while (p >= 0);
	return txt;	
}

ccTypes = {RR: true, RV: true};

